#include<bits/stdc++.h>
#define ll unsigned long long
using namespace std;
ll vis[10000+100];
ll cal(int a){
	queue<ll> que;
	que.push(1),que.push(2);
	memset(vis,0,sizeof vis);
	vis[1] = 1; vis[2] = 2;
	while(true){
		ll now = que.front(); que.pop();
		if(now%a==0) return now/a;
		for(int i = 0;i<3;i++){
			ll buf = now*10+i;
			ll buff = buf%a;
			if(vis[buff]) continue;
			vis[buff] = buf;
			que.push(buf);
