#include<bits/stdc++.h>
using namespace std;
const long long upper = 1e11;
const int maxm = 2e7+10;
const int mod = 1e9+7;
int add(int a,int b){return (a+b)%mod;}
int mul(long long a,long long b){return (a%mod)*(b%mod)%mod;}
int inv(long long a){ return a==1?1:mul(mod-mod/a,inv(mod%a)); }
int inv2 = inv(2);
bool p[maxm];
int pri[maxm];//
int phi[maxm];//
void init(){
	phi[1] = 1;
	for(int i =2;i<maxm;i++){
		if(!p[i]){
			pri[++pri[0]] = i;
			phi[i] = i-1;
		}
		for(int j = 1;j<=pri[0]&&i*pri[j]<maxm;j++){
			p[i*pri[j]] = true;
			if(i%pri[j]) phi[i*pri[j]] = phi[i]*(pri[j]-1);
			else{
				phi[i*pri[j]] = phi[i]*pri[j];
				break;
			}
		}
	}
	for(int i = 2;i<maxm;i++) phi[i] = add(phi[i],phi[i-1]);//,
}
map<long long,int> mp;
int solve(long long m){
	if(m<maxm) return phi[m];//
	if(mp[m]) return mp[m];//
	int ret = mul(m,mul(m+1,inv2));
	for(long long l = 2,r;l<=m;l = r+1) {//
		r = m/(m/l);
		ret = add(ret,mod-mul(solve(m/l),r-l+1));
	}	
	return mp[m] = ret;
}
int main(){
	init();
	int ans = 0;
	for(long long x = 2;x<=upper;x<<=1) {//2
		cout<<x<<endl;
		ans = add(ans,solve(upper/x));
		ans = add(ans,mod-1);
	}	
	cout<<ans<<endl;
	return 0;
}
