bool dp2[maxn][maxn][maxm];//ijmask
int last[maxn][maxn][maxm];//ij
vector<int> bit[maxm|10];//mask
vector<int> mat[maxm|10];//
pair<int,int> con[maxm|10];//mask
int addmask[maxm|10];//maskmask
