#include<bits/stdc++.h>
using namespace std;
int n,m;
double p;
double pow_p[100];
int pow_2[100];
int mat[100][100];
void init(){
    scanf("%d%d%lf",&n,&m,&p);
    p/=100;
    pow_2[0] = 1;
    pow_p[0] = 1;
    for(int i = 1;i<100;i++) pow_p[i] = pow_p[i-1]*p,pow_2[i] = pow_2[i-1]<<1;
    for(int i = 1,from,to;i<=m;i++) {
        scanf("%d%d",&from,&to);
        mat[--from][--to]++;
        mat[to][from]++;
    }
}
const int maxm = 1<<15;
double dp[maxm];
int has[100];
int sub[100];
void get(int state){
    dp[state] = 1;
    has[0] = 0;
    for(int i = 0;i<n;i++) if(state&(1<<i)) has[++has[0]] = i;
    if(has[0]==1) return ;
    for(int now = state&(state-1);now;now = (now-1)&state){
        if((now&(1<<has[1]))==0) continue;
        sub[0] = 0;
        for(int i = 1;i<=has[0];i++) if(now&(1<<has[i])) sub[++sub[0]] = has[i];
