        for(int i = 1;i<=has[0];i++)
            for(int j = 1;j<=sub[0];j++){
                if(now&(1<<has[i])) continue;
                cnt+=mat[has[i]][sub[j]];
            }
        dp[state]-=dp[now]*pow_p[cnt];
