#include<bits/stdc++.h>
using namespace std;
const int maxm = 105;
int s[maxm],d[maxm];
int main(){
	int n,t;
	cin>>n>>t;
	int ind,sum = 1e8;
	for(int i = 1;i<=n;i++){
		cin>>s[i]>>d[i];
		if(s[i]>=t){
			if(sum>s[i]){
				sum = s[i];
				ind = i;
			}
		}else {
			int nee = t-s[i];
			if(nee%d[i]!=0) nee = (nee/d[i]+1)*d[i];
