	cout<<":("<<endl;
	exit(0);
}
int main(){
	cin>>len>>que;
	if(len&1) NO();
	if(que[0]=='?') que[0] = '(';
	if(que[len-1]=='?') que[len-1] = ')';
	if(que[0]!='(') NO();
	if(que[len-1]!=')') NO();
	if(len==2) {
		puts("()");
		return 0;
	}
	int l = 0,r = 0,has = 0;
	for(int i = 1;i<len-1;i++){
		if(que[i]=='(') l++;
		else if(que[i]==')') r++;
		else has++;
	}
	if(abs(l-r)>has) NO();
	l = (len-2)/2 - l;
	for(int i = 1;i<len-1;i++){
